INSERT INTO `nha_tuyen_dung` VALUES (1,'Công ty Cổ phần Công nghệ Truyền thông Việt Nam (VNTT)','Luôn luôn đổi mới để phát triển','https://salt.topdev.vn/5p3NtKkXcm4EsozOhB92k-Pc6h4XYA2ruuM8Gq-ww9g/fit/384/1000/ce/1/aHR0cHM6Ly9hc3NldHMudG9wZGV2LnZuL2ltYWdlcy8yMDI0LzA0LzAzL1RvcERldi1Mb2dvLVZOVFQtLS1FbmdsaXNoLS0tVGllbi1OZ3V5ZW4tMTcxMjExMzYyMS5wbmc','[\"Công ty Cổ phần Công nghệ & Truyền thông Việt Nam (tên tiếng Anh: VIET NAM TECHNOLOGY & TELECOMMUNICATION JOINT STOCK COMPANY. Tên viết tắt: VNTT) là công ty được thành lập với sự góp vốn của 02 cổ đông sáng lập: Tổng Công ty Đầu tư và Phát triển Công nghiệp (Becamex IDC), Tập đoàn Bưu chính Viễn thông Việt Nam (VNPT). VNTT là đơn vị đáp ứng toàn diện, đa dạng các nhu cầu về Viễn thông – Công nghệ thông tin (ICT); Dịch vụ trung tâm dữ liệu eDatacenter; Dịch vụ MEP cho các doanh nghiệp.\", \"Trong thời đại công nghệ 4.0, VNTT đã có những bước phát triển mạnh dạn mở đầu cho xu hướng chuyển dịch sang lĩnh vực phát triển các giải pháp thông minh. Hiện VNTT đang trong giai đoạn phát triển và triển khai các giải pháp quản lý thông minh trong các khu công nghiệp do Becamex đầu tư trên khắp cả nước. Trong đó phải kể đến như: phần mềm quản lý Trung tâm điều hành thông minh IOC Bình Dương, IOC Becamex; các giải pháp thông minh như: Hệ thống chiếu sáng công cộng thông minh, Bãi đỗ xe thông minh, Quản lý toàn nhà thông minh và nhà máy nước thải thông minh…\", \"VNTT đã chuẩn hóa quy trình quản lý và cung cấp dịch vụ theo tiêu chuẩn quốc tế, đạt chứng nhận ISO 9001:2015 và ISO 27001:2022, liên tục nhiều năm liền đạt Top 10 Công ty Công nghệ Việt Nam uy tín và đạt Giải thưởng Thành phố thông minh Việt Nam 2023.\", \"Website: https://vntt.com.vn/\"]','100-499','Vietnam','[\"Đóng bảo hiểm và các chế độ theo luật đầy đủ\", \"Bảo hiểm tai nạn 24/24\", \"Lương tháng 13\", \"Thưởng KPI hàng quý\", \"Phụ cấp cơm trưa 50,000đ/ngày\", \"Chế độ thăm hỏi ốm đau, hiếu hỉ, các ngày lễ Tết …\", \"Chương trình gắn kết Nhân viên hàng quý\", \"Du lịch, hội thao hàng năm\", \"Khám sức khỏe định kỳ bệnh viện quốc tế Becamex\", \"Xe đưa đón Nhân viên từ Hồ Chí Minh – Bình Dương\", \"Có ký túc xá cho Nhân viên ở xa.\"]','Bình Dương','[\"Số 02, Đường Tiên Phong 03, Khu Công nghệ cao Maple Tree, Phường Hoà Phú, Thành phố Thủ Dầu Một, Tỉnh Bình Dương\", \"., Tỉnh Bình Dương\"]','0933498759','https://topdev.vn//nha-tuyen-dung/cong-ty-co-phan-cong-nghe-truyen-thong-viet-nam-vntt-94657?src=topdev_companies&medium=intotal','https://vntt.com.vn/','https://cdn.topdev.vn/v4/assets/images/company/cover_2.webp',NULL,NULL),(2,'Ngân hàng TMCP An Bình (ABBANK)','ABBANK','https://cdn.topdev.vn/v4/assets/images/hotjobs-icon.svg',NULL,NULL,NULL,NULL,'Quận Đống Đa, Hà Nội',NULL,'0914594927','https://topdev.vn//nha-tuyen-dung/ngan-hang-tmcp-an-binh-abbank-91848?src=topdev_companies&medium=intotal',NULL,'https://salt.topdev.vn/JlspM33Y3vZETkX01Ix7qge2RExbn5W0_0KxJt26Rsk/fit/1920/1000/ce/1/aHR0cHM6Ly9hc3NldHMudG9wZGV2LnZuL2ltYWdlcy8yMDIwLzEyLzEwL2NvdmVyRU1SWkdTR3hXNWMxMUdQdk42bG5jY0hxN21qMmdZTVctYjVqcHYuanBlZw',NULL,NULL),(3,'SoftRoad Việt Nam','TOP IT Jobs for Developers in Vietnam','https://salt.topdev.vn/BqP10Hya2BkK46Tu17Zo61jRF8pJWBW7uz46nMhlks0/fit/384/1000/ce/1/aHR0cHM6Ly9hc3NldHMudG9wZGV2LnZuL2ltYWdlcy8yMDIyLzEyLzA2L1RvcERldi0xMTgxMTg2NzEzNTM3MTQzNjgyOTk3NTU1NTE5MzMxMjU0NDE4NTQ5MDc2N24tMTY3MDMwMzEyMy5qcGc',NULL,NULL,NULL,NULL,'Quận Cầu Giấy, Hà Nội',NULL,'0922454241','https://topdev.vn//nha-tuyen-dung/softroad-viet-nam-93864?src=topdev_companies&medium=intotal',NULL,'https://salt.topdev.vn/JsAOdP6_J9-eip-wxDAVoCwgx2Tq8NNHtC2MP7czYrw/fit/1920/1000/ce/1/aHR0cHM6Ly9hc3NldHMudG9wZGV2LnZuL2ltYWdlcy8yMDIzLzA2LzI3L1RvcERldi1zb2Z0cm9hZC0xNjg3ODI4MjgzLnBuZw',NULL,NULL),(4,'Galaxy Studio','Galaxy Studio - \"Phim như sống – Sống như phim”','/uploads/1744601438939.png','[\"Gia nhập thị trường Phát hành phim & Hệ thống cụm rạp chiếu phim từ năm 2003 và được tư vấn thiết kế, lắp đặt bởi các chuyên gia hàng đầu, Công ty Cổ phẩn Phim Thiên Ngân